<?const VITE_HOST='http://localhost:1111';function add_vite() {wp_enqueue_script('app.min.js','/wp-content/themes/fls-theme/assets/build/js/app.min.js',array(),null,true);wp_enqueue_style('app.min.css','/wp-content/themes/fls-theme/assets/build/css/app.min.css',array(),null,'all');wp_enqueue_style('vite-custom-css','/wp-content/themes/fls-theme/assets/build/css/custom.css',array(),null,'all');wp_enqueue_script('vite-custom-js','/wp-content/themes/fls-theme/assets/build/js/custom.js',array(),null,true);};add_action('wp_enqueue_scripts', 'add_vite');function add_type_module_attribute($tag, $handle) {if ( 'app.min.js' === $handle) {return str_replace('<script', '<script type="module"', $tag);};return $tag;};add_filter('script_loader_tag', 'add_type_module_attribute', 10, 2);?>